package ar.edu.unju.fi.aplicacion.Punto8;

public class Punto8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int a=0,b;
    System.out.println("Multiplos de 4 menores o iguales a 100");
	do{
	a++;	
	b= a *4;	
	System.out.println(b);	
	}while(b< 100);
	
	}

}
